import React,{Component} from 'react'
import { Button, Form, Select } from 'antd';


const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };
const tailLayout = {
    wrapperCol: {
      offset: 8,
      span: 16,
    },
};



class TransactionForm extends Component {

  onSubmit(values) {
    console.log(values);
  }

  render() {
      return (
        <Form
          {...layout}
          name="basic"
          initialValues={{
            remember : false 
          }}
          onFinish={this.onSubmit}

          >
              <Form.Item
                label="From"
                name="from"
                rules={[
                    {
                        required: true,
                        message: 'Please select a user'
                    }
                ]}
              >
                <Select>
                    <Select.Option value="john">john</Select.Option>
                </Select>

              </Form.Item>
              <Form.Item
                label="To"
                name="to"
                rules={[
                    {
                        required: true,
                        message: 'Please select a user'
                    }
                ]}
              >
                <Select>
                    <Select.Option value="john">john</Select.Option>
                </Select>

              </Form.Item>
        </Form>
      )
  }
}
export default TransactionForm
