import React,{Component} from 'react'
import {Button,Alert,InputNumber, Form, Select } from 'antd';


const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };
const tailLayout = {
    wrapperCol: {
      offset: 8,
      span: 16,
    },
};



class TransactionForm extends Component {

  constructor(props){
    super(props);
    this.state = ({
      showLoader : false,
      showErrorCard : false,
      alertMessage : ""
    })
  }

  onSubmit = (values) => {
    this.toggleLoader();
    const url = "http://localhost:4000/transactions";
    const Data = {
        from : values.from,
        to : values.to,
        amount : values.amount
    }
    const params = {
        headers: {
            "Content-Type" : "application/json"
        },
        body : Data,
        method : "POST"
    }
    fetch(url,params)
    .then(res => res.json())
    .then(data => {
        console.log(data);
        this.toggleLoader();
        this.props.onClear();
    })
    .catch(err => {

      this.setState({
        showLoader : false,
        showErrorCard : true,
        alertMessage : err
      })
    })

  }
  toggleLoader = () => {
    this.setState({
      showLoader : !this.state.showLoader
    })
  }
  users = () => {
    return this.props.users.map(user => 
         <Select.Option key={user.id} value={user.name}>{user.name}</Select.Option>)
  }

  onClose = (e) => {
    console.log(e,"error card closed");
  }

  errorAlert = () => {
    return <Alert
      message="Invalid Transaction"
      description={this.state.alertMessage}
      type="error"
      closable
      onClose={this.onClose}
    />
  }

  render() {
      return (
        <Form
          {...layout}
          name="basic"
          initialValues={{
            remember : false 
          }}
          onFinish={this.onSubmit}

          >
              <Form.Item
                label="From"
                name="from"
                rules={[
                    {
                        required: true,
                        message: 'Please select a user'
                    }
                ]}
              >
                <Select>
                    {this.users()}
                </Select>

              </Form.Item>
              <Form.Item
                label="To"
                name="to"
                rules={[
                    {
                        required: true,
                        message: 'Please select a user'
                    }
                ]}
              >
                <Select>
                    {this.users()}
                </Select>

              </Form.Item>
              <Form.Item
                name={['amount']}
                label="Amount"
                rules={[
                {
                    type: 'number',
                    min: 10,
                    max: 10000000,
                },
                ]}
                >
                <InputNumber />
              </Form.Item>
              <Form.Item {...tailLayout}>
                <Button loading={this.state.showLoader} type="primary" htmlType="submit">
                    Send
                </Button>
            </Form.Item>
            {this.state.showErrorCard ? this.errorAlert() : null}
        </Form>
      )
  }
}
export default TransactionForm
