import React from 'react';
import './App.css';

function App() {
  return (
    <div style={style.wrapper}>
      <header style={style.headerStyle}>
        <label style={style.appTitle}>VIMATION</label>
      </header
      >
      <div style={style.projectNameWrapperStyle} id="project-name-wrapper">
        <input style={style.projectName} id="project-name" placeholder="ENTER PROJECT NAME" type="text"></input>
        <input type="button" value="SAVE"></input>
      </div>

    </div>
  );
}

export default App;


const style = {
  wrapper : {
    backgroundColor : '#101010',
    color: 'white',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    overflowX: 'hidden'
  },
  appTitle : {
    fontSize : '2.2em',
    color : 'white',
    marginLeft: '3.2em',
    fontWeight : 'bold'
  },
  headerStyle : {
    display: 'flex',
    width : "100%",
    padding : '1.4em',
    flexDirection: 'row',
    alignItems : 'space-between'
  },
  projectNameWrapperStyle : {
    backgroundColor : '#8766F5',
    width : "100%",
    padding : "1.2em",
    display: 'flex',
    flexDirection: 'column',
    alignItems : 'space-between'
  },
  projectName : {
    fontSize : '1.6em',
    backgroundColor : 'transparent',
    color : 'white',
    borderWidth : "0px",
    border: 'none',
    outline : 'none',
    marginLeft : "3.7em",


  }
}
