import React, {Component} from 'react';
import {Button} from 'antd';
import './upload.css'
import 'antd/dist/antd.css';
class Upload extends Component {

    constructor (props){
        super(props);
        this.state = {
            'saved' : false,
            'showCenterUpload' : false,
            'videoFrames' : []
        }
    }

    saveFile(){
        console.log("Work saved");
    }

    render(){
        return (
            <div style={style.wrapper}>
                <header style={style.headerStyle}>
                    <section id="logo">
                        <svg height="50" width="50">
                            <circle cx="25" cy="25" r="17.5" stroke-width="3" fill="#6F53E6" />
                        </svg>
                        <label style={style.appTitle}>VIMATION</label>
                    </section>

                </header>
                <div style={style.projectNameWrapperStyle} id="project-name-wrapper">
                    <input style={style.projectName} id="project-name" placeholder="ENTER PROJECT NAME" type="text"></input>
                    <input style={style.save} onClick={this.saveFile} type="button" value="SAVE"></input>
                </div>
                <section style={style.framesView} id="framesView">

                    <Button type="primary">Upload Video</Button>

                </section>
             </div>
        )
    }
}

export default Upload

const style = {
    wrapper : {
      backgroundColor : '#101010',
      color: 'white',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      overflowX: 'hidden'
    },
    appTitle : {
      fontSize : '1.9em',
      color : 'white',
      fontWeight : 'bold',

    },
    headerStyle : {
      display: 'flex',
      width : "100%",
      padding : '1.4em',
      flexDirection: 'row',
      alignItems : 'space-between'
    },
    projectNameWrapperStyle : {
      backgroundColor : '#8766F5',
      width : "100%",
      padding : "1.2em",
      display: 'flex',
      flexDirection: 'row',

      alignItems : 'space-between',
      justifyContent: 'space-between'
    },
    projectName : {
      fontSize : '1.6em',
      backgroundColor : 'transparent',
      borderWidth : "0px",
      outline : 'none',
      marginLeft : "4.7em",
      color : 'white'

    },
    framesView : {
        height : '100%',
        width : '100%',
        display: 'flex',
        flexDirection: 'column',
        alignItems : 'center',
        justifyContent : 'center'

    },
    uploadBtn : {
       fontSize : '2.6em',
       outline: 'none',
       border : 'none',
       backgroundColor : 'transparent',
       color : 'white'

    },
    save : {
      marginRight : '6em',
      backgroundColor : 'transparent',
      outline: 'none',
      border : 'none',
      color : 'white',
      fontSize : '1.3em',
      fontWeight : '2em'
    }
  }
