import React, { Component } from 'react';
import TransactionsTable from './Table'
import { Button,  Modal } from 'antd';
import TransactionForm from './Form'

class TransactionPage extends Component {

  constructor(props){
      super(props);

      this.state = {
          showLoader : true ,
          visible : false
      }

      // make fetch to transactions route
      this.dataSource = [
        {
          key: '1',
          name: 'Mike',
          age: 32,
          address: '10 Downing Street',
        },
        {
          key: '2',
          name: 'John',
          age: 42,
          address: '10 Downing Street',
        },
      ];

  }

  showModal = () => {
    this.setState(
      { visible : true }
    )
  }

  handleOk = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  handleCancel = () => {
    this.setState({
      visible: false,
    });
  }

  render() {
      return (
        <div style={styles.pageWrapper}>
            <div>
                <Button type="default" onClick={this.showModal}>New Transaction</Button>
            </div><br/>
            <TransactionsTable dataSource={this.dataSource}/>
            <Modal
              title = "New Transaction"
              visible = {this.state.visible}
              onOk={this.handleOk}
              onCancel={this.handleCancel}
              footer={[]}
            >

              <TransactionForm onClear={this.handleCancel}/>

            </Modal>
        </div>
      )
  }

}
export default TransactionPage;

const styles = {
    pageWrapper : {
        display: 'flex',
        flexDirection: 'column',
        padding : '0 2.2em',

        height : '100%',
        width : '100%',

    }
}
