import React, { Component } from 'react';
import TransactionsTable from './Table'
import { Button,  Modal } from 'antd';

class TransactionPage extends Component {

  constructor(props){
      super(props);

      this.state = {
          showLoader : true ,
          visibile : true
      }


      // make fetch to transactions route
      this.dataSource = [
        {
          key: '1',
          name: 'Mike',
          age: 32,
          address: '10 Downing Street',
        },
        {
          key: '2',
          name: 'John',
          age: 42,
          address: '10 Downing Street',
        },
      ];

  }

  render() {
      return (
        <div style={styles.pageWrapper}>
            <div>
                <Button type="default">New Transaction</Button>
            </div><br/>
            <TransactionsTable dataSource={this.dataSource}/>
        </div>
      )
  }

}
export default TransactionPage;

const styles = {
    pageWrapper : {
        display: 'flex',
        flexDirection: 'column',
        padding : '0 2.2em',

        height : '100%',
        width : '100%',

    }
}
