import React,{Component} from 'react'
import {Button,InputNumber, Form, Select } from 'antd';


const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };
const tailLayout = {
    wrapperCol: {
      offset: 8,
      span: 16,
    },
};



class TransactionForm extends Component {


  onSubmit(values) {
    console.log(values);
    const url = "http://localhost:4000/transactions";
    const Data = {
        from : values.from,
        to : values.to,
        amount : values.amount
    }
    const params = {
        headers: {
            "content-type" : "application/json; charset=UTF - 8"
        },
        body : Data,
        method : "POST"
    }
    fetch(url,params)
    .then(res => res.json())
    .then(data => {
        console.log(data);
        this.props.onClear();
    })
    .catch(err => console.log(err))

  }

  users = () => {
    console.log(this.props.users);
    return this.props.users.map(user => 
         <Select.Option key={user.id} value={user.name}>{user.name}</Select.Option>)
  }

  render() {
      return (
        <Form
          {...layout}
          name="basic"
          initialValues={{
            remember : false 
          }}
          onFinish={this.onSubmit}

          >
              <Form.Item
                label="From"
                name="from"
                rules={[
                    {
                        required: true,
                        message: 'Please select a user'
                    }
                ]}
              >
                <Select>
                    {this.users()}
                </Select>

              </Form.Item>
              <Form.Item
                label="To"
                name="to"
                rules={[
                    {
                        required: true,
                        message: 'Please select a user'
                    }
                ]}
              >
                <Select>
                    {this.users()}
                </Select>

              </Form.Item>
              <Form.Item
                name={['amount']}
                label="Amount"
                rules={[
                {
                    type: 'number',
                    min: 10,
                    max: 10000000,
                },
                ]}
                >
                <InputNumber />
              </Form.Item>
              <Form.Item {...tailLayout}>
                <Button type="primary" htmlType="submit">
                    Send
                </Button>
            </Form.Item>
        </Form>
      )
  }
}
export default TransactionForm
