import React, { Component } from 'react';
import UsersTable from './Table'

class UsersPage extends Component {

  constructor(props){
      super(props);

      this.state = {
          showLoader : true 
      }

      // make fetch to transactions route
      this.dataSource = [
        {
          key: '1',
          name: 'Mike',
          age: 32,
          address: '10 Downing Street',
        },
        {
          key: '2',
          name: 'John',
          age: 42,
          address: '10 Downing Street',
        },
      ];

  }

  componentDidMount(){
    const url = 'https:localhost/4000/users';
    fetch(url)
    .then((users) => {
      console.log(users);
    })

  }

  render() {
      return (
        <div style={styles.pageWrapper}>
            <UsersTable dataSource={this.dataSource}/>
        </div>
      )
  }

}
export default UsersPage;

const styles = {
    pageWrapper : {
        display: 'flex',
        flexDirection: 'column',
        padding : '0 2.2em',

        height : '100%',
        width : '100%',

    }
}
